# Login by account/password and return access_token
def login_access_token(user, password) -> (str | None, str | None):
    headers = {
        "content-type": "application/x-www-form-urlencoded; charset=UTF-8",
        "user-agent": "MiFit6.14.0 (M2007J1SC; Android 12; Density/2.75)",
        "app_name": _APP_NAME,
        "appname": _APP_NAME,
        "appplatform": "android_phone",
        "x-hm-ekv": "1",
        "hm-privacy-ceip": "false"
    }
    login_data = {
        'emailOrPhone': user,
        'password': password,
        'state': 'REDIRECTION',
        'client_id': 'HuaMi',
        'country_code': 'CN',
        'token': 'access',
        'redirect_uri': 'https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html',
    }
    # 等同 http_build_query，默认使用 quote_plus 将空格转为 '+'
    query = urllib.parse.urlencode(login_data)
    plaintext = query.encode('utf-8')
    # 执行请求加密
    cipher_data = encrypt_data(plaintext, HM_AES_KEY, HM_AES_IV)

    url1 = 'https://api-user.zepp.com/v2/registrations/tokens'
    r1 = _request_with_retry("post", url1, data=cipher_data, headers=headers, allow_redirects=False)
    if r1.status_code != 303:
        return None, "登录异常，status: %d" % r1.status_code
    try:
        location = r1.headers["Location"]
        code = get_access_token(location)
        if code is None:
            return None, "获取accessToken失败 %s" % get_error_code(location)
    except:
        return None, f"获取accessToken异常:{traceback.format_exc()}"
    return code, None


# 获取登录code
def get_access_token(location):
    code_pattern = re.compile("(?<=access=).*?(?=&)")
    result = code_pattern.findall(location)
    if result is None or len(result) == 0:
        return None
    return result[0]


def get_error_code(location):
    code_pattern = re.compile("(?<=error=).*?(?=&)")
    result = code_pattern.findall(location)
    if result is None or len(result) == 0:
        return None
    return result[0]


# 获取北京时间
def get_beijing_time():
    target_timezone = pytz.timezone('Asia/Shanghai')
    # 获取当前时间
    return datetime.now().astimezone(target_timezone)


# 格式化时间
def format_now():
    return get_beijing_time().strftime("%Y-%m-%d %H:%M:%S")


# 获取时间戳
def get_time():
    current_time = get_beijing_time()
    return "%.0f" % (current_time.timestamp() * 1000)


# 获取login_token，app_token，userid
def grant_login_tokens(access_token, device_id, is_phone=False) -> (str | None, str | None, str | None, str | None):
    url = "https://account.huami.com/v2/client/login"
    headers = {
        "app_name": _APP_NAME,
        "x-request-id": f"{str(uuid.uuid4())}",
        "accept-language": "zh-CN",
        "appname": _APP_NAME,
        "cv": "50818_6.14.0",
        "v": "2.0",
        "appplatform": "android_phone",
        "content-type": "application/x-www-form-urlencoded; charset=UTF-8",
    }
    if is_phone:
        data = {
            "app_name": _APP_NAME,
            "app_version": "6.14.0",
            "code": access_token,
            "country_code": "CN",
            "device_id": device_id,
            "device_model": "phone",
            "grant_type": "access_token",
            "third_name": "huami_phone",
        }
    else:
        data = {
            "allow_registration=": "false",
            "app_name": _APP_NAME,
            "app_version": "6.14.0",
            "code": access_token,
            "country_code": "CN",
            "device_id": device_id,
            "device_model": "android_phone",
            "dn": "account.zepp.com,api-user.zepp.com,api-mifit.zepp.com,api-watch.zepp.com,app-analytics.zepp.com,api-analytics.huami.com,auth.zepp.com",
            "grant_type": "access_token",
            "lang": "zh_CN",
            "os_version": "1.5.0",
            "source": "com.xiaomi.hm.health:6.14.0:50818",
            "third_name": "email",
        }
    response = _request_with_retry("post", url, data=data, headers=headers)
    try:
        resp = response.json()
    except Exception:
        body_preview = (response.text or "").strip()[:300]
        return None, None, None, f"客户端登录失败：非 JSON 响应（http={response.status_code}, body={body_preview or 'empty'}）"

    if not isinstance(resp, dict):
        return None, None, None, f"客户端登录失败：响应格式异常（{type(resp).__name__}）"

    result = resp.get("result")
    token_info = resp.get("token_info") if isinstance(resp.get("token_info"), dict) else {}
    _login_token = token_info.get("login_token")
    _app_token = token_info.get("app_token")
    _userid = token_info.get("user_id")

    if result == "ok" and _login_token and _app_token and _userid:
        return _login_token, _app_token, _userid, None

    details = []
    if result is not None:
        details.append(f"result={result}")
    code = resp.get("code")
    if code is not None:
        details.append(f"code={code}")
    message = resp.get("message") or resp.get("msg") or resp.get("description")
    if message:
        details.append(f"message={message}")
    missing_keys = [k for k in ("login_token", "app_token", "user_id") if not token_info.get(k)]
    if missing_keys:
        details.append("token_info缺少=" + ",".join(missing_keys))
    if not details:
        details.append(json.dumps(resp, ensure_ascii=False)[:300])
    return None, None, None, "客户端登录失败：" + " | ".join(details)


# 获取app_token 用于提交数据变更
def grant_app_token(login_token: str) -> (str | None, str | None):
    url = f"https://account-cn.huami.com/v1/client/app_tokens?app_name=com.xiaomi.hm.health&dn=api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com&login_token={login_token}"
    headers = {'User-Agent': 'MiFit/5.3.0 (iPhone; iOS 14.7.1; Scale/3.00)'}
    resp = _request_with_retry("get", url, headers=headers)
    if resp.status_code != 200:
        return None, "请求异常：%d" % resp.status_code
    resp = resp.json()

    result = resp.get("result")
    if result != "ok":
        error_code = resp.get("error_code")
        return None, "请求失败：%s" % error_code
    app_token = resp['token_info']['app_token']
    return app_token, None


